import React, { useState } from "react";

function App(props) {

  const newTime = new Date().toLocaleTimeString();
  const [initial , updatedTime] = useState(props.time, newTime);

  return (
    <div className="container">
      <h1>{initial}</h1>
      {setInterval(updatedTime,1000)}
      <button>Get Time</button>
    </div>
  );
}

export default App;
